import React, { useState, useEffect } from 'react';

export default function toDoForm (props){
// class TodoForm extends React.Component {

  // constructor(props) {
  //   super(props);
  //   this.state = { item: {} };
  // }

  let [items, setItems] = useState([])

  function handleInputChange (e) {
    setItems({ item: {...items.item, [e.target.name]: e.target.value } });
  };

   function handleSubmit (e) {
    e.preventDefault();
    e.target.reset();
    props.handleSubmit(items.item);
    const item = {};
    setItems({item});
  };

  // render() {
    return (
      <>
        <h3>Add Item</h3>
        <form onSubmit={handleSubmit}>
          <label>
            <span>To Do Item</span>
            <input
              name="text"
              placeholder="Add To Do List Item"
              onChange={handleInputChange}
            />
          </label>
          <label>
            <span>Difficulty Rating</span>
            <input defaultValue="1" type="range" min="1" max="5" name="difficulty" onChange={this.handleInputChange} />
          </label>
          <label>
            <span>Assigned To</span>
            <input type="text" name="assignee" placeholder="Assigned To" onChange={this.handleInputChange} />
          </label>
          <button>Add Item</button>
        </form>
      </>
    );
  }


